insert into perfil (nome, tipo_investimento, cidade, cpf, bairro, tipo_imovel, tempo_mudanca, gasto_previsto, renda_declarada) values ('Flavia', '1', 'São Paulo', '42825103829', 'Pinheiros', '2', 2, 2500, 3000);
insert into dica (titulo, dica, views, video) values ('Como é definida a taxa de juros dos financiamentos?', 'Os financiamentos diferem de acordo com o valor do imóvel e a renda do comprador. Essas informações são balizadoras para definir qual taxa de juros será aplicada em cada contrato. Alguns imóveis novos podem ser enquadrados no programa Minha Casa, Minha Vida, desde que respeitando as exigências estipuladas pela lei.', 900, 1);
insert into dica (titulo, dica, views, video) values ('Em que casos é possível usar o FGTS?', 'O FGTS pode ser usado para adquirir um imóvel próprio, pagar prestações, amortizar ou liquidar saldo devedor de um financiamento bancário de imóvel residencial em andamento. O imóvel tem que estar localizado na cidade em que o cliente mora ou na mesma região metropolitana.', 900, 1);
insert into dica (titulo, dica, views, video) values ('Até que percentual do valor do imóvel pode ser financiado?', 'Alguns bancos oferecem linhas de financiamento de até 90% do valor do imóvel, incluindo os do âmbito do programa Minha Casa, Minha Vida.', 900, 0);
insert into dica (titulo, dica, views, video) values ('Qual a diferença entre financiar pela construtora e por um banco?', 'A maioria das construtoras não cobra juros sobre o valor financiado durante a construção do imóvel, mas cobra uma taxa de obras, que vai aumentando conforme evolui a construção. Esses contratos são atualizados pela variação do Índice Nacional de Custo da Construção (INCC), a partir da data de assinatura até a conclusão do pagamento. Depois de o imóvel pronto, são aplicados juros mais uma atualização pelo Índice Geral de Preços – Mercado (IGP-M), até o fim do financiamento. O prazo de financiamento das construtoras é menor do que o dos bancos: 60 meses, podendo chegar a 96 meses. Já os bancos podem ser contratados quando o imóvel já está pronto. Eles cobram uma taxa de juros acrescida da Taxa Referencial (TR), índice utilizado para corrigir cadernetas de poupança, atualmente menor que o IGP-M. Hoje, há bancos que concedem linhas de crédito para serem quitadas em até 420 meses (35 anos).', 900, 1);
insert into dica (titulo, dica, views, video) values ('Quais são as condições exigidas para financiar um imóvel?', 'O tomador de crédito deve ter no mínimo 18 anos, estar com o nome limpo nas empresas de proteção de crédito e comprovar renda suficiente para pagamento das futuras prestações do empréstimo.', 900, 1);
insert into dica (titulo, dica, views, video) values ('É possível quitar o financiamento antes do prazo combinado ou abater parte da dívida durante o pagamento das parcelas?', 'É permitido e recomendável. Sempre que se obtém crédito a juros, o ideal é solicitar o menor valor necessário e pagar no menor tempo possível.', 900, 1);
